import React, { useState } from 'react';
import './CategoryCarousel.css';

const CategoryCarousel = ({ title, images, category }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrevious = () => {
    setCurrentIndex((prev) => 
      prev === 0 ? images.length - 1 : prev - 1
    );
  };

  const goToNext = () => {
    setCurrentIndex((prev) => 
      prev === images.length - 1 ? 0 : prev + 1
    );
  };

  const goToSlide = (index) => {
    setCurrentIndex(index);
  };

  // Show 3 images at a time on desktop, 1 on mobile
  const getVisibleImages = () => {
    const visibleCount = window.innerWidth > 768 ? 3 : 1;
    const visibleImages = [];

    for (let i = 0; i < visibleCount; i++) {
      const imageIndex = (currentIndex + i) % images.length;
      visibleImages.push(images[imageIndex]);
    }

    return visibleImages;
  };

  return (
    <div className="category-carousel">
      <h3 className="category-title">{title}</h3>
      <div className="carousel-container">
        <button 
          className="carousel-nav carousel-prev" 
          onClick={goToPrevious}
          aria-label="Previous images"
        >
          &#8249;
        </button>

        <div className="carousel-track">
          <div 
            className="carousel-slides"
            style={{ 
              transform: `translateX(-${currentIndex * (100 / (window.innerWidth > 768 ? 3 : 1))}%)` 
            }}
          >
            {images.map((image, index) => (
              <div key={index} className="carousel-slide">
                <img 
                  src={image.src} 
                  alt={image.alt}
                  className="carousel-image"
                />
                <div className="image-overlay">
                  <p className="image-description">{image.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <button 
          className="carousel-nav carousel-next" 
          onClick={goToNext}
          aria-label="Next images"
        >
          &#8250;
        </button>
      </div>

      {/* Indicators */}
      <div className="carousel-indicators">
        {images.map((_, index) => (
          <button
            key={index}
            className={`indicator ${index === currentIndex ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            aria-label={`Go to image ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default CategoryCarousel;
