import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleWhatsAppClick = () => {
    const message = "Hi! I'm interested in your photography services. Can you please provide more information?";
    const phoneNumber = "919440018476"; // Your actual WhatsApp number
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link to="/" className="logo" onClick={closeMobileMenu}>
          Classic Graphics
        </Link>

        <button 
          className="mobile-menu-toggle"
          onClick={toggleMobileMenu}
          aria-label="Toggle mobile menu"
        >
          â˜°
        </button>

        <ul className={`nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
          <li>
            <Link 
              to="/" 
              className={location.pathname === '/' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Home
            </Link>
          </li>
          <li>
            <Link 
              to="/portfolio" 
              className={location.pathname === '/portfolio' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Portfolio
            </Link>
          </li>
          <li>
            <Link 
              to="/blog" 
              className={location.pathname === '/blog' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Blog
            </Link>
          </li>
          <li>
            <Link 
              to="/wedding" 
              className={location.pathname === '/wedding' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Wedding
            </Link>
          </li>
          <li>
            <Link 
              to="/event" 
              className={location.pathname === '/event' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Event
            </Link>
          </li>
          <li>
            <Link 
              to="/birthday" 
              className={location.pathname === '/birthday' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Birthday
            </Link>
          </li>
          <li>
            <Link 
              to="/portrait" 
              className={location.pathname === '/portrait' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Portrait
            </Link>
          </li>
          <li>
            <Link 
              to="/nature" 
              className={location.pathname === '/nature' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Nature
            </Link>
          </li>
          <li>
            <Link 
              to="/contact" 
              className={location.pathname === '/contact' ? 'active' : ''}
              onClick={closeMobileMenu}
            >
              Contact
            </Link>
          </li>
        </ul>

        <button 
          className="whatsapp-btn"
          onClick={handleWhatsAppClick}
          title="Contact us on WhatsApp"
        >
          <div className="whatsapp-icon"></div>
          WhatsApp
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
